var Benchmark = require('benchmark'),
    suite = new Benchmark.Suite;

    // long length. length === 756
var longstra = '';
// same as above
var longstrb = '';

var test1 = function(){
    var c = longstra + longstrb 
};
var test2 = function(){
    var c = '';
    c += longstra;
    c += longstrb;
};
var test3 = function(){
    var c = [longstra, longstrb].join('');
};
var test4 = function(){
    var c = longstra.concat(longstrb);
};
// 添加测试
suite.add('+         ', function() {
    test1();
}).add('+=        ', function() {
    test2();
})
.add('arr.join  ', function() {
    test3();
})
.add('str.concat', function() {
    test4();
}).on('cycle', function(event) {
    console.log(String(event.target));// add listeners
}).on('complete', function() {
    console.log('Fastest is ' + this.filter('fastest').map('name'));
}).run({ 'async': false });// run async

